    exp_grid = list(map(lambda x: x.split(" "), "- - - - - - 5 2 4 7 3 6 9 8 1 - - - - - -\n- - - - - - 8 9 6 1 2 5 4 3 7 - - - - - -\n- - - - - - 7 1 3 8 4 9 5 2 6 - - - - - -\n- - - - - - 1 3 7 6 8 4 2 9 5 - - - - - -\n- - - - - - 4 5 2 9 1 7 8 6 3 - - - - - -\n- - - - - - 9 6 8 2 5 3 1 7 4 - - - - - -\n6 1 9 8 7 3 2 4 5 3 7 8 6 1 9 7 8 2 5 3 4\n5 4 7 2 1 6 3 8 9 5 6 1 7 4 2 6 3 5 8 9 1\n3 8 2 4 9 5 6 7 1 4 9 2 3 5 8 4 1 9 6 2 7\n1 3 6 7 5 4 8 9 2 - - - 5 6 3 1 2 4 9 7 8\n8 7 4 3 2 9 5 1 6 - - - 2 7 1 9 5 8 3 4 6\n2 9 5 1 6 8 4 3 7 - - - 8 9 4 3 6 7 1 5 2\n9 6 8 5 3 7 1 2 4 8 6 5 9 3 7 8 4 1 2 6 5\n7 2 3 6 4 1 9 5 8 4 7 3 1 2 6 5 7 3 4 8 9\n4 5 1 9 8 2 7 6 3 9 1 2 4 8 5 2 9 6 7 1 3\n- - - - - - 8 4 1 7 2 9 5 6 3 - - - - - -\n- - - - - - 2 3 7 1 5 6 8 9 4 - - - - - -\n- - - - - - 6 9 5 3 8 4 7 1 2 - - - - - -\n- - - - - - 4 1 9 6 3 7 2 5 8 - - - - - -\n- - - - - - 3 8 2 5 4 1 6 7 9 - - - - - -\n- - - - - - 5 7 6 2 9 8 3 4 1 - - - - - -".split("\n")))
    solver = SoheiSudokuSolver(**data.puzzle_dict)
    res_grid = solver.solve().solution_data.get('solution_grid', [])
    assert Grid(exp_grid) == res_grid